"use strict";

function task1() {
  // Оголошення функції task1
  var age; // Змінна для збереження віку

  while (true) {
    // Безкінечний цикл (вихід через break)
    age = prompt("Введіть ваш вік:"); // Запитуємо вік у користувача

    if (age === null) break; // Якщо натиснуто "Скасувати" — вихід

    age = parseInt(age); // Перетворення введення на ціле число

    if (!isNaN(age) && age >= 0) {
      // Перевірка: число і не менше 0
      if (age <= 11) alert("Ви дитина."); // Вік 0–11 → дитина
      else if (age <= 17) alert("Ви підліток."); // Вік 12–17 → підліток
        else if (age <= 59) alert("Ви дорослий."); // Вік 18–59 → дорослий
          else alert("Ви пенсіонер."); // Вік 60+ → пенсіонер

      break; // Вихід з циклу після успішного введення
    } else {
      alert("Некоректне значення. Спробуйте ще."); // Повідомлення, якщо введення не число
    }
  }
}

function task2() {
  // Оголошення функції task2
  while (true) {
    // Нескінченний цикл
    var digit = prompt("Введіть цифру від 0 до 9:"); // Запитуємо цифру

    if (digit === null) break; // Якщо натиснули "Скасувати" — вихід

    if (!isNaN(digit) && digit >= 0 && digit <= 9) {
      // Перевірка: це число від 0 до 9
      switch (digit) {
        // Вибір символа за цифрою
        case '0':
          alert("Символ: )");
          break;
        // Якщо 0 → )

        case '1':
          alert("Символ: !");
          break;
        // Якщо 1 → !

        case '2':
          alert("Символ: @");
          break;
        // Якщо 2 → @

        case '3':
          alert("Символ: #");
          break;
        // Якщо 3 → #

        case '4':
          alert("Символ: $");
          break;
        // Якщо 4 → $

        case '5':
          alert("Символ: %");
          break;
        // Якщо 5 → %

        case '6':
          alert("Символ: ^");
          break;
        // Якщо 6 → ^

        case '7':
          alert("Символ: &");
          break;
        // Якщо 7 → &

        case '8':
          alert("Символ: *");
          break;
        // Якщо 8 → *

        case '9':
          alert("Символ: (");
          break;
        // Якщо 9 → (
      }

      break; // Вихід після правильного введення
    } else {
      alert("Помилка: введіть цифру від 0 до 9."); // Якщо не цифра — показати помилку
    }
  }
}

function task3() {
  // Оголошення функції task3
  var start = parseInt(prompt("Початок діапазону:")); // Введення початкового числа

  var end = parseInt(prompt("Кінець діапазону:")); // Введення кінцевого числа

  var sum = 0; // Змінна для зберігання суми

  if (!isNaN(start) && !isNaN(end)) {
    // Перевірка правильності обох чисел
    if (start > end) {
      var _ref = [end, start];
      start = _ref[0];
      end = _ref[1];
    } // Обмін місцями, якщо потрібно


    for (var i = start; i <= end; i++) {
      sum += i;
    } // Цикл: додаємо всі числа в діапазоні


    alert("\u0421\u0443\u043C\u0430: ".concat(sum)); // Виводимо результат
  } else {
    alert("Некоректні числа"); // Повідомлення про помилку
  }
}

function task4() {
  // Оголошення функції task4
  var a = parseInt(prompt("Перше число:")); // Введення першого числа

  var b = parseInt(prompt("Друге число:")); // Введення другого числа

  if (!isNaN(a) && !isNaN(b)) {
    // Перевірка обох чисел
    while (b !== 0) {
      // Алгоритм Евкліда
      var temp = b; // Збереження b у тимчасову змінну

      b = a % b; // Знаходимо залишок від ділення

      a = temp; // Присвоюємо a попереднє b
    }

    alert("\u041D\u0421\u0414: ".concat(a)); // Вивід результату
  } else {
    alert("Помилка вводу"); // Повідомлення про помилку
  }
}

function task5() {
  // Оголошення функції task5
  var number = parseInt(prompt("Введіть число:")); // Введення числа

  if (!isNaN(number) && number > 0) {
    // Перевірка, чи число > 0
    var result = ""; // Змінна для збереження дільників

    for (var i = 1; i <= number; i++) {
      // Цикл від 1 до введеного числа
      if (number % i === 0) result += i + " "; // Якщо ділиться — додаємо до результату
    }

    alert("\u0414\u0456\u043B\u044C\u043D\u0438\u043A\u0438: ".concat(result)); // Виводимо всі дільники
  } else {
    alert("Некоректне число"); // Повідомлення про помилку
  }
}

function task6() {
  // Оголошення функції task6
  var num = prompt("Введіть п’ятизначне число:"); // Запит п’ятизначного числа

  if (num.length === 5 && !isNaN(num)) {
    // Перевірка: довжина = 5 та це число
    var isPal = true; // Початково вважаємо, що паліндром

    for (var i = 0; i < num.length / 2; i++) {
      // Цикл до середини числа
      if (num[i] !== num[num.length - 1 - i]) {
        // Порівнюємо симетричні символи
        isPal = false; // Якщо не рівні — не паліндром

        break; // Перериваємо перевірку
      }
    }

    alert(isPal ? "Це паліндром" : "Це не паліндром"); // Виводимо результат
  } else {
    alert("Введіть п’ятизначне число"); // Якщо умова не виконана — помилка
  }
}

function task7() {
  // Оголошення функції task7
  var amount = parseFloat(prompt("Сума покупки:")); // Введення суми

  var discount = 0; // Змінна для знижки

  if (!isNaN(amount) && amount > 0) {
    // Перевірка: число > 0
    if (amount >= 500) discount = 7; // Якщо 500 і більше — 7%
    else if (amount >= 300) discount = 5; // Якщо 300–499 — 5%
      else if (amount >= 200) discount = 3; // Якщо 200–299 — 3%

    var _final = amount - amount * discount / 100; // Обчислення остаточної ціни


    alert("\u0417\u043D\u0438\u0436\u043A\u0430: ".concat(discount, "%\n\u0414\u043E \u043E\u043F\u043B\u0430\u0442\u0438: ").concat(_final.toFixed(2), " \u0433\u0440\u043D")); // Вивід
  } else {
    alert("Некоректна сума"); // Повідомлення про помилку
  }
}

function task8() {
  // Оголошення функції task8
  var positives = 0,
      negatives = 0,
      zeros = 0,
      even = 0,
      odd = 0; // Лічильники для всіх типів

  for (var i = 1; i <= 10; i++) {
    // 10 чисел
    var num = parseFloat(prompt("\u0427\u0438\u0441\u043B\u043E #".concat(i, ":"))); // Запит числа

    if (isNaN(num)) continue; // Якщо не число — пропустити

    if (num > 0) positives++; // Додатнє
    else if (num < 0) negatives++; // Від’ємне
      else zeros++; // Нуль

    if (num % 2 === 0) even++; // Парне
    else odd++; // Непарне
  }

  alert("\u0414\u043E\u0434\u0430\u0442\u043D\u0456: ".concat(positives, "\n\u0412\u0456\u0434\u2019\u0454\u043C\u043D\u0456: ").concat(negatives, "\n\u041D\u0443\u043B\u0456: ").concat(zeros, "\n\u041F\u0430\u0440\u043D\u0456: ").concat(even, "\n\u041D\u0435\u043F\u0430\u0440\u043D\u0456: ").concat(odd)); // Вивід
}

function task9() {
  // Оголошення функції task9
  var days = ["Понеділок", "Вівторок", "Середа", "Четвер", "П’ятниця", "Субота", "Неділя"]; // Масив днів

  var i = 0; // Лічильник днів

  while (true) {
    // Нескінченний цикл
    var next = confirm("".concat(days[i], ". \u0425\u043E\u0447\u0435\u0448 \u043F\u043E\u0431\u0430\u0447\u0438\u0442\u0438 \u043D\u0430\u0441\u0442\u0443\u043F\u043D\u0438\u0439 \u0434\u0435\u043D\u044C?")); // Питання користувачу

    if (!next) break; // Якщо "Скасувати" — вихід

    i = (i + 1) % days.length; // Переходимо до наступного дня (по колу)
  }
}